INSERT INTO `job` (`idjob`, `tgl_job`, `posisi_job`, `namapt`, `lokasi_job`, `diskripsi`, `status_job`) VALUES
('JOB0005', '2023-03-03', 'Mining Manager', 'PT. MINERAL ALAM ABADI', 'Head Office - Jakarta', '<p>Job Description :</p><p>1. Menyusun dan melaksanakan strategi operasi penambangan secara baik dan benar, sesuai dengan peraturan perundang-undangan yang berlaku.</p><p>2. Menyusun rencana kerja dan anggaran (budget) operasi penambangan berdasarkan atas rencana penambangan (mine planning) jangka pendek, menengah dan panjang.</p><p>3. Memastikan pencapaian target produksi dengan memperhatikan efisiensi dan efektifitas keseluruhan biaya operasi penambangan.</p><p>4. Melakukan analisis kinerja operasi penambangan secara detail dan menyusun upaya-upaya perbaikan/penyempurnaan secara berkelanjutan, serta melaporkannya secara rutin kepada management.</p><p>5. Menyusun strategi pengembangan dan perbaikan seluruh infrastruktur penambangan (termasuk stockpile, jalan angkut, pelabuhan dan pengelolaan lingkungan).</p><p>6. Memastikan seluruh SOP dan kebijakan perusahaan telah dilaksanakan dengan sebaik-baiknya.</p><p>7. Menyusun strategi dan memimpin koordinasi kerja seluruh departemen (Operation, Geology, Plant, HSE, HRGA, GC, QA dan lain-lain)</p><p>8. Menyampaikan saran dan informasi kepada management tentang implementasi berbagai peraturan perundang-undangan yang terkait secara langsung atau tidak langsung dengan pertambangan.</p><p>9. Menyusun laporan rekonsiliasi raw material bijih nikel yang masuk ke dalam stockpile dan langsung dipasarkan ke customer.</p><p>10. Menyiapkan rencana integrasi antara kebutuhan market dan rencana produksi dan jaminan kualitas dari spesifikasi bijih nikel dengan Manager Geologi dan Manager Operation</p><p>11. Menyusun Laporan Cadangan Mineral tahunan dari setiap project aktif untuk kebutuhan pelaporan internal maupun kebutuhan eksternal terkait dengan perizinan dan kebutuhan compliance</p><p>12. Menyelesaikan evaluasi Kelayakan dan Uji Tuntas pada wilayah pertambangan yang prospektif</p><p>13. Mewakili perusahaan dalam melakukan koordinasi dengan KTT dan seluruh instansi pemerintah, berdasarkan atas instruksi management.</p><p>14. Melaporkan master plan conceptual berupa trade-off studi penambangan khususnya project baru berupa due diligence, kebutuhan peralatan tambang, rekonsiliasi penambangan dan estimasi biaya&nbsp;</p><p>15. Membuat laporan Analisa biaya, profit/loss antara perencanaan dan production actual.</p><p>&nbsp;</p><p>Requirement :&nbsp;</p><p>1. Min. S1 Teknik, Pertambangan&nbsp;</p><p>2. Lebih dari 5 tahun pengalaman di industri pertambangan nikel dan minimum telah menduduki posisi Manager level selama 2 tahun.&nbsp;</p><p>3. Mampu melakukan analisis dan simulasi perhitungan secara detail</p><p>4. Memiliki kapabilitas dalam melakukan multi-tasking (managerial, engineering, budgeting, costing, planning, directing, monitoring, controlling, organizing, problem-solving, managing people, reporting dan lain-lain)</p><p>5. Menguasai software tambang sejenis: Vulcan, Surpac, Micromine, Datamine, XPAC, MineSched, Spry Schedule, GIS software dan sejenisnya.&nbsp;</p><p>6. Memahami berbagai faktor eksternal yang dapat mempengaruhi keberlangsungan operasi penambangan</p><p>7. Memiliki leadership yang kuat serta mampu bekerja dibawah tekanan</p><p>8. Mampu berbahasa Inggris (lisan dan tulisan)</p><p>9. Bersedia terlibat dalam site-visit meninjau lokasi project tambang aktif di semua wilayah di Indonesia</p>', 'Non Aktif'),
('JOB001', '2022-12-12', 'MAINTENANCE PLANNER SUPERVISOR', 'PT. MINERAL ALAM ABADI', 'JAKARTA', '<p>Requirements</p><ol><li>Laki-laki, berusia maksimal 40 tahun</li><li>Memiliki pengalaman di industry alat berat produksi China</li><li>Pengalaman 2-3 tahun dibidangnya</li><li>Memahami software planner</li><li>Memiliki pengalaman di Industry Mining</li><li>Mampu mengontrol budget dan cost dalam proses repair &amp; maintenance</li></ol>', 'Aktif'),
('JOB002', '2022-12-15', 'TRANSLATOR MANDARIN', 'PT. BANGUNAN TEKNIK GRUP', 'SITE OBI', '<ul><li>Bachelor\'s degree in any field.</li><li>Required language Mandarin (excellent in speaking and writing)</li><li>Professional work ethics and attitude</li><li>Mandarin language certificate is an advantage</li><li>Other overseas graduates and local graduates are welcome to apply</li><li>Have a good communication skill</li><li>Proficient with Microsoft Office</li><li>Excellent organizational and attention to detail.</li><li>Strong analytical and problem-solving skills.</li></ul>', 'Aktif'),
('JOB003', '2022-12-16', 'TRANSLATOR MANDARIN', 'PT. MINERAL ALAM ABADI', 'HO, APL', '<ul><li>Bachelor\'s degree in any field.</li><li>Required language Mandarin (excellent in speaking and writing)</li><li>Professional work ethics and attitude</li><li>Mandarin language certificate is an advantage</li><li>Other overseas graduates and local graduates are welcome to apply</li><li>Have a good communication skill</li><li>Proficient with Microsoft Office</li><li>Excellent organizational and attention to detail.</li><li>Strong analytical and problem-solving skills.</li></ul>', 'Aktif'),
('JOB004', '2022-12-16', 'EXTERNAL RELATION FOREMANnnn', 'PT. MITRA MINERAL PERKASA', 'SITE OBI', '<ol><li>Pendidikan minimal S1 &nbsp;Sosial, Hukum, Komunikasi, Public Realtion, dan Jurusan relevan &nbsp;lainnya&nbsp;</li><li>Pengalaman kerja &nbsp;minimal 3 tahundi industri pertambangan terutaman &nbsp;untuk masalah hubungan eksternal&nbsp;</li><li>Memiliki kemampuan negosiasi, &nbsp;komhunikasi yang &nbsp;kuat baik lisan maupun tulisan, &nbsp;paham budaya &nbsp;lokal &nbsp;&amp; birokrasinya</li><li>&nbsp;Memiliki pengetahuan dan keterampilan yang &nbsp;kuat dalam &nbsp;UU Pertambangan, Perizinan, dan peraturan wajib lainnya</li><li>Bersedia &nbsp;untuk bekerja &nbsp;secara &nbsp;mobilisasi/travel&nbsp;</li><li>Memiliki kemampuan MS &nbsp;Office, &nbsp;pemetaan sosial, pengembangan komunitas, CSR dan resolusi &nbsp;konflik</li></ol>', 'Aktif');
